mapper.select:

where t.del_flag = '0'
 and t.task_id = #{taskId}
 and t.out_dept = #{outDept}
 and t.out_subject = #{outSubject}
 and t.in_dept = #{inDept}
 and t.in_subject = #{inSubject}
 order by t.create_time desc

mapper.update:
update t_budget_oa_adjustment
task_id = #{taskId}, out_dept = #{outDept}, out_subject = #{outSubject}, in_dept = #{inDept}, in_subject = #{inSubject}, amount = #{amount}, remark = #{remark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where id = #{id}

mapper.delete:
update t_budget_oa_adjustment
set del_flag = '2' , update_by = #{userName}, update_time = SYSDATE()
where id = #{id}
