mapper.select:

where t.del_flag = '0' and t.dept_id in (SELECT dept_id from t_budget_item_dept where item_id = 6)
 and t.task_id = #{taskId}
 and t.dept_id = #{deptId}
 and t.budget = #{budget}
 and t.description = #{description}
 and t.audit_budget = #{auditBudget}
 and t.audit_description = #{auditDescription}
 and t.last_budget = #{lastBudget}
 and t.last_accumulated_expenses = #{lastAccumulatedExpenses}
 and t.adjustment_budget = #{adjustmentBudget}
 and t.adjustment_description = #{adjustmentDescription}
 and t.projected_costs = #{projectedCosts}
 and t.status = #{status}
 and d.parent_id = #{parentId}
 and t.status != 1  
 and (t.status = 3 or  t.status = 5 or  t.status = 6) 
 and (t.status = 3 or  t.status = 5 ) 

    and t.dept_id in

        #{id}


order by t.dept_id

SELECT t.id,t.task_id,t.dept_id,t.budget,t.description,t.audit_budget,t.audit_description,t.last_budget,t.last_accumulated_expenses,t.adjustment_budget,t.adjustment_description,t.projected_costs,t.`status`,t.create_by,t.create_time,t.update_by,t.update_time,t.del_flag,tk.`name` as taskName,d.dept_name as deptName, d.parent_id as parentId, pd.dept_name as parentName, u.nick_name as userName
FROM t_reporting_table_charity t
LEFT JOIN t_reporting_task tk ON t.task_id = tk.id
LEFT JOIN sys_dept d ON t.dept_id = d.dept_id
LEFT JOIN sys_dept pd on d.parent_id = pd.dept_id
LEFT JOIN sys_user u on t.create_by = u.user_name
where t.del_flag = '0' and t.dept_id in (SELECT dept_id from t_budget_item_dept where item_id = 6)
 and t.task_id = #{taskId}
 and t.dept_id = #{deptId}
 and t.budget = #{budget}
 and t.status = #{status}
 and d.parent_id = #{parentId}
 and t.status != 1  
 and (t.status = 3 or  t.status = 5 or  t.status = 6) 

    and t.dept_id in

        #{id}


UNION ALL
SELECT NULL AS id, NULL AS task_id, t.dept_id, SUM(t.budget) AS budget, NULL AS description, SUM(t.audit_budget) AS audit_budget, NULL AS audit_description, SUM(t.last_budget) AS last_budget, SUM(t.last_accumulated_expenses) AS last_accumulated_expenses, SUM(t.adjustment_budget) AS adjustment_budget, NULL AS adjustment_description, SUM(t.projected_costs) AS projected_costs, 5 AS `status`, NULL AS create_by, NOW() AS create_time, NULL AS update_by, NULL AS update_time, NULL AS del_flag, NULL AS taskName, CONCAT(d.dept_name, '合计') AS deptName, NULL AS parentId, NULL AS parentName, NULL as userName
FROM t_reporting_table_charity t
LEFT JOIN t_reporting_task tk ON t.task_id = tk.id
LEFT JOIN sys_dept d ON t.dept_id = d.dept_id
LEFT JOIN sys_dept pd on d.parent_id = pd.dept_id
where t.del_flag = '0' and t.dept_id in (SELECT dept_id from t_budget_item_dept where item_id = 6)
 and t.task_id = #{taskId}
 and t.dept_id = #{deptId}
 and t.budget = #{budget}
 and t.status = #{status}
 and d.parent_id = #{parentId}
 and t.status != 1  
 and (t.status = 3 or  t.status = 5 or  t.status = 6) 

    and t.dept_id in

        #{id}


GROUP BY t.dept_id
ORDER BY dept_id, create_time

mapper.update:
update t_reporting_table_charity
task_id = #{taskId}, dept_id = #{deptId}, budget = #{budget}, description = #{description}, audit_budget = #{auditBudget}, audit_description = #{auditDescription}, last_budget = #{lastBudget}, last_accumulated_expenses = #{lastAccumulatedExpenses}, adjustment_budget = #{adjustmentBudget}, adjustment_description = #{adjustmentDescription}, projected_costs = #{projectedCosts}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where id = #{id}

mapper.delete:
update t_reporting_table_charity
set del_flag = '2' , update_by = #{userName}, update_time = SYSDATE()
where id = #{id}
