mapper.select:

where t.del_flag = '0' and t.dept_id in (SELECT dept_id from t_budget_item_dept where item_id = 4)
 and t.task_id = #{taskId}
 and t.dept_id = #{deptId}
 and t.project_type = #{projectType}
 and t.number = #{number}
 and t.name like concat('%', #{name}, '%')
 and t.status = #{status}
 and d.parent_id = #{parentId}
 and t.status != 1  
 and (t.status = 3 or  t.status = 5 or  t.status = 6) 
 and (t.status = 3 or  t.status = 5 ) 

    and t.dept_id in

        #{id}


order by t.dept_id

SELECT t.id,t.task_id,t.dept_id,t.project_status,t.project_type,t.number,t.quantity,t.NAME,t.cooperative_unit,t.start_time,t.end_time,t.research_contents,t.deliverables,t.investment,t.completed_settlement,t.expected_settlement,t.intangible_assets,t.fixed_assets,t.fixed_assets_description,t.subtotal_costs,t.subtotal_costs_description,t.audit_settlement,t.audit_description,t.year1_settlement,t.year2_settlement,t.year3_settlement,t.year4_settlement,t.STATUS,t.remark,t.create_by,t.create_time,t.update_by,t.update_time,t.del_flag ,tk.`name` as taskName,d.dept_name as deptName, d.parent_id as parentId, pd.dept_name as parentName, u.nick_name as userName
FROM  t_reporting_table_research t
LEFT JOIN t_reporting_task tk on t.task_id = tk.id
LEFT JOIN sys_dept d on t.dept_id = d.dept_id
LEFT JOIN sys_dept pd on d.parent_id = pd.dept_id
LEFT JOIN sys_user u on t.create_by = u.user_name
where t.del_flag = '0' and t.dept_id in (SELECT dept_id from t_budget_item_dept where item_id = 4)
 and t.task_id = #{taskId}
 and t.dept_id = #{deptId}
 and t.project_type = #{projectType}
 and t.number = #{number}
 and t.name like concat('%', #{name}, '%')
 and t.status = #{status}
 and d.parent_id = #{parentId}
 and t.status != 1  
 and (t.status = 3 or  t.status = 5 or  t.status = 6) 

    and t.dept_id in

        #{id}


UNION ALL
SELECT NULL AS id, NULL AS task_id, t.dept_id, NULL AS project_status, NULL AS project_type, NULL AS number, SUM(t.quantity) AS quantity, '' AS `name`, NULL AS cooperative_unit, NULL AS start_time, NULL AS end_time, NULL AS research_contents, NULL AS deliverables, SUM(t.investment) AS investment, SUM(t.completed_settlement) AS completed_settlement, SUM(t.expected_settlement) AS expected_settlement, SUM(t.intangible_assets) AS intangible_assets, SUM(t.fixed_assets) AS fixed_assets, NULL AS fixed_assets_description, SUM(t.subtotal_costs) AS subtotal_costs, NULL AS subtotal_costs_description, SUM(t.audit_settlement) AS audit_settlement, NULL AS audit_description, SUM(t.year1_settlement) AS year1_settlement, SUM(t.year2_settlement) AS year2_settlement, SUM(t.year3_settlement) AS year3_settlement, SUM(t.year4_settlement) AS year4_settlement, 5 AS `status`, NULL AS remark, NULL AS create_by, NOW() AS create_time, NULL AS update_by, NULL AS update_time, NULL AS del_flag, NULL AS taskName, CONCAT(d.dept_name, '合计') AS deptName, NULL AS parentId, NULL AS parentName, NULL as userName
FROM  t_reporting_table_research t
LEFT JOIN t_reporting_task tk on t.task_id = tk.id
LEFT JOIN sys_dept d on t.dept_id = d.dept_id
LEFT JOIN sys_dept pd on d.parent_id = pd.dept_id
where t.del_flag = '0' and t.dept_id in (SELECT dept_id from t_budget_item_dept where item_id = 4)
 and t.task_id = #{taskId}
 and t.dept_id = #{deptId}
 and t.project_type = #{projectType}
 and t.number = #{number}
 and t.name like concat('%', #{name}, '%')
 and t.status = #{status}
 and d.parent_id = #{parentId}
 and t.status != 1  
 and (t.status = 3 or  t.status = 5 or  t.status = 6) 

    and t.dept_id in

        #{id}


GROUP BY t.dept_id
ORDER BY dept_id, create_time

mapper.update:
update t_reporting_table_research
task_id = #{taskId}, dept_id = #{deptId}, project_status = #{projectStatus}, project_type = #{projectType}, number = #{number}, quantity = #{quantity}, name = #{name}, cooperative_unit = #{cooperativeUnit}, start_time = #{startTime}, end_time = #{endTime}, research_contents = #{researchContents}, deliverables = #{deliverables}, investment = #{investment}, completed_settlement = #{completedSettlement}, expected_settlement = #{expectedSettlement}, intangible_assets = #{intangibleAssets}, fixed_assets = #{fixedAssets}, fixed_assets_description = #{fixedAssetsDescription}, subtotal_costs = #{subtotalCosts}, subtotal_costs_description = #{subtotalCostsDescription}, audit_settlement = #{auditSettlement}, audit_description = #{auditDescription}, year1_settlement = #{year1Settlement}, year2_settlement = #{year2Settlement}, year3_settlement = #{year3Settlement}, year4_settlement = #{year4Settlement}, status = #{status}, remark = #{remark}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where id = #{id}

mapper.delete:
update t_reporting_table_research
set del_flag = '2' , update_by = #{userName}, update_time = SYSDATE()
where id = #{id}
